import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  _user: {},
};

export const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    setUser: (state, user) => {
      state._user = user;
    },
  },
});

export const { setUser } = userSlice.actions;

export default userSlice.reducer;
